import axios from 'axios';

const baseURL = 'http://www.omdbapi.com/?apikey=3b54b69a&s=James Bond&type=movie';


export const fetchData = async (endpoint) => {
  try {
    const response = await axios.get(endpoint);
    return response.data;
  } catch (error) {
    throw error;
  }
};
